import Link from 'next/link';
import Head from 'next/head';

function InlineStyles () {
    return(
        <>
        <style jsx>
    {`.addeventatc_icon{
          display: none;
        }
    .playkit-volume-control-bar .playkit-progress {
    background-color: #00a058 !important;
      }

    .playkit-player .playkit-seek-bar .playkit-progress-bar .playkit-progress {
    background-color: #00a058 !important;
      }
    .playkit-frame-preview {
    display: none!important;
    }
    html {
    scroll-behavior: smooth;
    }`}
  </style>
        </>
    )
}

export default function Layout ({children, title, live}) {
    return (
        <>

        <Head>

        <meta charset="utf-8" />
    <link rel="apple-touch-icon" sizes="76x76" href="../assets/img/apple-icon.png"/>
    <link rel="icon" type="image/png" href="../assets/img/favicon.png"/>
    <meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1" />
    {/* <!-- Primary Meta Tags --> */}
    <title>Seminario en línea | Medical Beyond</title>
    <meta name="title" content="Seminario en línea | Medical Beyond" />
    <meta name="description" content="<?php echo $metaDescription; ?>" />
    {/* <!-- Open Graph / Facebook --> */}
    <meta property="og:type" content="website"/>
    <meta property="og:url" content="https://live.medicalbeyond.mx/<?php echo $metaSlug; ?>"/>
    <meta property="og:title" content="Seminario en línea | Medical Beyond"/>
    <meta property="og:description" content="<?php echo $metaDescription; ?>" />
    <meta property="og:image" content="https://live.medicalbeyond.mx/<?php echo $metaSlug; ?>/assets/img/<?php echo $metaImg; ?>.jpg" />
    {/* <!-- Twitter --> */}
    <meta property="twitter:card" content="summary_large_image" />
    <meta property="twitter:url" content="https://live.medicalbeyond.mx/<?php echo $metaSlug; ?>" />
    <meta property="twitter:title" content="Seminario en línea | Medical Beyond" />
    <meta property="twitter:description" content="<?php echo $metaDescription; ?>" />
    <meta property="twitter:image" content="https://live.medicalbeyond.mx/<?php echo $metaSlug; ?>/assets/img/<?php echo $metaImg; ?>.jpg" />
    <meta content='width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0, shrink-to-fit=no' name='viewport' />
    {/* <!--     Fonts and icons     --> */}
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;800&amp;display=swap" rel="stylesheet" />
    <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.3.1/css/all.css" integrity="sha384-mzrmE5qonljUremFsqc01SB46JvROS7bZs3IO2EmfFsd15uHvIt+Y8vEf7N7fWAU" crossorigin="anonymous" />
    {/* <!-- CSS Files --> */}
    <link href="/css/bootstrap.min.css" rel="stylesheet" />
    <link href="/css/now-ui-kit.css?v=1.1.0" rel="stylesheet" />
    <link href="/css/brand1.css?v=1.0.1" rel="stylesheet" />
    <link href="/css/icons.css" rel="stylesheet" />
    <link rel="stylesheet" type="text/css" href="/css/parsley.css" />
    <link href="/css/sweetalert2.min.css" rel="stylesheet" type="text/css"/>
    <InlineStyles/>

        </Head>
        <div className="signup-page">
        {/* <!-- Navbar --> */}
        <nav className="navbar navbar-expand-lg bg-dark-grey fixed-top py-0">
            <div className="container">
                <div className="navbar-translate">
                    <Link href="/">
                        <a className="navbar-brand py-4">
                        <img className="" src="/img/sinergis_w.svg" width="150"/>
                    </a>
                    </Link>
                </div>
                {live && 
                    <Link href='/'>
                        <a className="btn" style={{textDecoration:'none'}}>Salir</a>
                    </Link>
                }
            </div>
        </nav>
        {/* <!-- End Navbar --> */}
        {children}

        <footer className="footer py-4">
            <div className="container">
                <div className="pt-auto pb-4">
                    <img className="logo-footer" src="/img/sinergis_F.svg" width="100"/>

                </div>
            </div>
        </footer>
    {/*    Core JS Files   */}
        <script src="/js/core/jquery.3.2.1.min.js" type="text/javascript"></script>
        <script src="/js/core/popper.min.js" type="text/javascript"></script>
        <script src="/js/core/bootstrap.min.js" type="text/javascript"></script>
        <script src="/js/plugins/moment.min.js"></script>
        <script src="/js/plugins/bootstrap-selectpicker.js" type="text/javascript"></script>
        <script src="/js/now-ui-kit.js" type="text/javascript"></script>
        {/* <script type="text/javascript" src="js/player-menu.js"></script> */}
    </div>
</>
    )
}
